var sql = 
"""
    select
        a.*
    from
        lite_user a
    where a.rec_del_flag = '0' 
     and a.dept_id =  #{body.deptId} 

    ?{orderByColumn, order by a.${orderByColumn} ${direction == 0 ? 'asc' : direction == 1 ? 'desc' : ''}}
    ?{!orderByColumn, order by a.rec_sort_num asc}
"""

var result = db.select(sql);

var sort = 5;
result.each(data => {
    var newData = {
        id:data.id,
        recSortNum:sort
    }
    db.table("lite_user").primary("id").update(newData);
    sort = sort + 5;
})

return 1;

