var toTree = (list,pid) => select t.*,toTree(list,t.id) children from list t where t.pid = pid;

var sql =
"""
    select * from
    (
        select a.id,a.id org_id,a.name_txt name,a.code_txt code,'ORG' type,pid,(10000+a.rec_sort_num) rec_sort_num from lite_org a

        union all
        select a.id,a.org_id org_id,a.name_txt name,a.code_txt code,'DEPT' type,case when a.pid=a.org_id then a.org_id else a.pid end pid,a.rec_sort_num from lite_dept a

    ) a
    order by a.rec_sort_num
"""

var list = toTree(db.select(sql),'-1');

var result = {
    list: list,
    total: list.getLength()
};

return result;
