api.script:
/*
    测试循环List
*/
var list = [1,2,3,4,5];
var sum = 0;
for(val in list){
    sum = sum + val;
}
return sum;

/*
     测试循环Map
 */
 var map = {
     key1 : 1,
     key2 : 2,
     key3 : 3
 };
 var sum = 0;
 var keys = '';
 for(key,value in map){
     sum = sum + value;
     keys = keys + key
 }
 return keys + '-' + sum;

/*
     测试循环
 */
 var sum = 0;
 for(val in range(0,100)){   //包括0 包括100
     if(val > 90){
         break;  //跳出循环
     }
     if(val % 3 == 0){
         continue;   //进入下一次循环
     }
     sum = sum + val;
 }
 return sum;

var map = {
     key1 : 1,
     key2 : 2,
     key3 : 3
 };
 var sum = 0;
 var keys = '';
 map.each((key,value) => {
     keys += key;
     sum += value;

 })
 return keys + '-' + sum;

var index = 0;
 var sum = 0;
 while(index < 100){
     sum += index++;
 }
 return sum
